function striptg ($tag_name = 'em', $sContent = '', $class = '')
{

    if(!empty($sContent))
    {
        $r = '';
        $r2 = '';
        $tag_name = preg_match('#<*\s*(\S+)#i', $tag_name, $t) ? $t[1] : 'em';

        if(!empty($class))
        {
            $r = '<span class="'.$class.'">';
            $r2 = '</span>';
        }

        // $sContent = str_ireplace('<'.$tag_name.'>', $r, $sContent);
        // $sContent = str_ireplace('</'.$tag_name.'>', $r2, $sContent);
        $sContent = preg_replace('#<\s*'.$tag_name.'( [^>]+)*>#', $r, $sContent);
        $sContent = preg_replace('#<\s*/\s*'.$tag_name.'\s*>#', $r2, $sContent);
    }

    return $sContent;
}






//Обработка
function changeHeadBlock($sContent, $sRegExp, $sBlock) {
    if (preg_match($sRegExp, $sContent)) {
        return preg_replace($sRegExp, $sBlock, $sContent);
    } else {
        return str_replace('<head>', '<head>' . $sBlock, $sContent);
    }
}

function descr_chng ($sContent, $sDescr, $iprop='')
{
    if(!empty($iprop)) $iprop = ' itemprop="'.$iprop.'"';
    return changeHeadBlock($sContent, '#<meta[^>]+name[^>]{1,7}description[^>]*>#siU', '<meta name="description" content="' . $sDescr . '"'.$iprop.' />');
}
function h1_chng ($sContent, $aSEODataH1, $iprop='')
{
    $aSEODataH1 = str_replace("\\", "\\\\", $aSEODataH1);
    if(!empty($iprop))
        {
            $iprop = ' itemprop="'.$iprop.'"';
            return $sContent = preg_replace('#<h1([^>]*>).*(</h1>)#siU', '<h1'.$iprop.'$1' . $aSEODataH1 . '$2', $sContent);
        }
        else return $sContent = preg_replace('#(<h1[^>]*>).*(</h1>)#siU', '$1' . $aSEODataH1 . '$2', $sContent);
}


